(function(){tinymce.PluginManager.requireLangPack('wikilinks');tinymce.create('tinymce.plugins.WikilinksPlugin',{init:function(ed,url){ed.addCommand('mceWikilinks',function(){var se=ed.selection;if(se.isCollapsed()&&!ed.dom.getParent(se.getNode(),'A'))return;if(ed.dom.getParent(se.getNode(),'A')&&!$(se.getNode()).hasClass('wikilink')){return}ed.windowManager.open({file:url+'/dialog.htm',width:320+ed.getLang('wikilinks.delta_width',0),height:300+ed.getLang('wikilinks.delta_height',0),inline:1},{plugin_url:url})});ed.addButton('wikilinks',{title:'wikilinks.desc',cmd:'mceWikilinks',image:url+'/img/wikilinks.gif'});ed.onNodeChange.add(function(ed,cm,n,co){cm.setDisabled('wikilinks',co&&n.nodeName!='A'&&!$(n).hasClass('wikilink'));cm.setActive('wikilinks',n.nodeName=='A'&&$(n).hasClass('wikilink'))})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Wikilinks plugin',author:'NetRelations',authorurl:'http://www.netrelations.se/',infourl:'',version:"1.0"}}});tinymce.PluginManager.add('wikilinks',tinymce.plugins.WikilinksPlugin)})();